import { View, Text, FlatList } from 'react-native'
import React, { useEffect, useState } from 'react'
import  { fetchAllCountry } from '../services/axios'

const HomeScreen = () => {
    const [Countries, setCountries] = useState([])
    useEffect(() => {
       fetchCountries()
    }, [])

    const fetchCountries = async () => {
        const [data,err] = await fetchAllCountry('all');
        if (data) {
          setCountries(data);
        }else{
        }
      };

      const renderItem = (country,i) => { 
        let currencies = Object.keys(country.currencies)
        console.log(country.currencies,'----',currencies);



        return(
        <View style={{flex:1,backgroundColor:'cyan',padding:10,marginBottom:10}}>
          <Text>{country.name.common}</Text>
          <Text>{country.name.common}</Text>
        </View>
        )
       }
  return (
    <View>
      <FlatList
      data={Countries}
      renderItem={({item,index})=>renderItem(item,index)}
      />
    </View>
  )
}

export default HomeScreen
