import { View, Text, FlatList, RefreshControl, StyleSheet, TouchableOpacity } from 'react-native'
import React, { useCallback, useEffect, useState } from 'react'
import  { Apifetch } from '../services/axios'
import AsyncStorage from '@react-native-async-storage/async-storage'
import Picker from '../components/Picker'
import ActivityIndicate from '../components/Activityind'
import SearchBar from '../components/Picker'
import { useNavigation } from '@react-navigation/native'

const HomeScreen = () => {
  const navigation = useNavigation();
  const [Countries, setCountries] = useState([])
  const [Currency, setCurrencies] = useState('')
  const [Region, setRegion] = useState([])
  const [Loading, setLoading] = useState(false);

  useEffect(() => {
      fetchCountries()
      setLoading(true);

  }, [Loading])

    const fetchCountries = async () => {
      /*
      * Will fetch all countries if async Store is empty
      * clear async store if any errors are encountered
      **/
      try{
          const StoreData =await AsyncStorage.getItem('CountryData')

          if(StoreData!==null){
            setCountries(JSON.parse(StoreData));
            setLoading(false)

          } else{
            const [data,err] = await Apifetch('all');

            if(data){
              setCountries(data);
              await AsyncStorage.setItem('CountryData',JSON.stringify(data));
              setLoading(false)

            }else {
              setLoading(false)
              AsyncStorage.clear();

            }

          }
      }catch(error){
        console.log(error);
        setLoading(false)
        AsyncStorage.clear();

      }
    }

      const onRefresh = () => {
        //set isRefreshing to true
        fetchCountries()
        setLoading(true)
        // and set isRefreshing to false at the end of your callApiMethod()
      }
      const renderItem = (countries,i) => { 
        const countriesList = countries?.item;
        //minimize the list of datasets

        const NativeNameList = Object.keys(countriesList.currencies).map((code)=>{
          const currencyData= countriesList.currencies[code]
          return(
            <>
               <Text style={styles.TextStyle}>{currencyData.name}</Text>
              <Text style={styles.TextStyle}>{currencyData.symbol}</Text>
            </>

          )
        })
        return(
        <TouchableOpacity onPress={()=>navigation.navigate('CountryDetails',{countriesList})} style={{flex:1,backgroundColor:'#D6E4E5',padding:10,marginBottom:10,borderRadius:10}}>
          <Text style={styles.TextStyle}>{countriesList.name.common}</Text>
          <View style={{flexDirection:'row',width:'100%',justifyContent:'space-between'}}>
            {NativeNameList}
          </View>
          <Text style={styles.TextStyle}>{countriesList.capital}</Text>
          <Text style={styles.TextStyle}>{countriesList.region}</Text>
        </TouchableOpacity>
        )
       }
  return (
    <View style={{flex:1,padding:10,backgroundColor:'#EFF5F5'}}>
      {/* <ActivityIndicate isLoading={Loading}/> */}
      <FlatList
      data={Countries}
      scrollsToTop={true}
      extraData={Countries}
      keyExtractor={(_,index) => index}
      renderItem={renderItem}
      ListHeaderComponent={({item})=>(
        <SearchBar setSearchValue={setCountries} regionData={Region}/>
        )}
      stickyHeaderIndices={[0]}
      onRefresh={onRefresh}
      refreshing={Loading}
      />
    </View>
  )
}
const styles = StyleSheet.create({
  TextStyle:{margin:5}
})
export default HomeScreen
