import { View, Text, TextInput, TouchableOpacity } from 'react-native'
import React, { useEffect, useState } from 'react'
import { Apifetch } from '../services/axios';


    const SearchBar = (props) => {

        const { setSearchValue,regionData }=props;

        const [inputValue, setInputValue] = useState('');

        const [isCancelled, setisCancelled] = useState('');

        const [filterdata, setfilterData] = useState([]);

        const [MainData, setMainData] = useState('');

        const [SelectedItem, setSelectedItem] = useState('');

        useEffect(() => {
          setisCancelled(false)
          handleSearch()

          return () => {
            setisCancelled(true)
          }
        }, [])
        console.log(props,"------->>>>");

          const handleSearch = async () => {
            try{
              const [data,err] = await Apifetch('region/europe');
              if (!isCancelled){
                setSearchValue(data);
                setMainData(data)
              }
            } catch(error){
              if(!isCancelled){
                console.log('request Failed: ' + error);
              }
            }

          };

          handleSearch()

        const handleInputChange = (text) => {
            setInputValue(text);
        };

        return (
          <>
          <View style={{flex:1,flexDirection:'row',width:'95%',padding:10,justifyContent:'space-between',backgroundColor:'#D6E4E5',margin:10,borderRadius:30}}>
            <TextInput
              placeholder="Enter a region name"
              onChangeText={handleInputChange}
              value={inputValue}
              style={{width:'100%'}}
            />
            {/* onPress={handleSearch} */}
            <TouchableOpacity onPress={handleSearch} style={{backgroundColor:'#EB6440',padding:10,marginRight:5,borderRadius:30,alignItems:'center',justifyContent:'center'}}>
              <Text>Search</Text>
            </TouchableOpacity>
          </View>

          </>

        );
      };





export default SearchBar
