import { View, Text, FlatList } from 'react-native'
import React, { useEffect, useState } from 'react'
import  { fetchAllCountry } from '../services/axios'

const HomeScreen = () => {
    const [Countries, setCountries] = useState([])
    const [Currencies, setCurrencies] = useState([])
    useEffect(() => {
       fetchCountries()
    }, [])

    const fetchCountries = async () => {
        const [data,err] = await fetchAllCountry('all');
        if (data) {
          setCountries(data);
          for (const currency in data[0].currencies) {
            console.log('jbsjdfbsjfbhs',data[currency]);
          }
        }else{
          console.log('error fetching countries');
        }
      };

      const renderItem = (countries,i) => { 
        const countriesList = countries.item
        // console.log(countries.item.currencies);
        for (const currencies in countriesList.currencies){
          setCurrencies(currencies)
          console.log(countriesList.currencies[currencies].name,'code---',currencies);
        }

        return(
        <View style={{flex:1,backgroundColor:'cyan',padding:10,marginBottom:10}}>
          <Text>{countriesList.name.common}</Text>
          <Text>{countriesList.currencies[Currencies].name}</Text>
          <Text>{countriesList.capital}</Text>
        </View>
        )
       }
  return (
    <View style={{flex:1,padding:10}}>
      <FlatList
      data={Countries}
      renderItem={renderItem}
      />
    </View>
  )
}

export default HomeScreen
