import { StyleSheet, Text, View } from 'react-native'
import React from 'react'

const CountryDetails = ({ route, navigation }) => {
  const {countriesList}=route.params;

  const utcToIst = (state)=>{
    const moment = require('moment-timezone');
    const utcOffset = moment.duration(state[0]).asMinutes();
    const istTime = moment().utcOffset(utcOffset).tz('Asia/Kolkata').format('YYYY-MM-DD HH:mm:ss');
  return istTime
  }
  console.log(utcToIst(countriesList.timezones),'--------------------------------------------------------');
  return (
    <View style={styles.container}>
      <Text>{countriesList.name.common}</Text>
      <Text>{countriesList.capital}</Text>
      <Text>{countriesList.region}</Text>
      <Text>{countriesList.timezones}</Text>
    </View>
  )
}

export default CountryDetails

const styles = StyleSheet.create({
    container:{backgroundColor:'#D6E4E5',padding:10,marginBottom:10,borderRadius:10}
})
